// Déclaration avec let (valeur modifiable)
let age = 25;
age = 26; // ✅ OK

// Déclaration avec const (constante)
const name = "Alice";
name = "Bob"; // ❌ Erreur : impossible de réassigner une constante

// ⚠️ Éviter var (ancienne syntaxe, portée globale)

// ✅ Bon (camelCase)
let firstName = "Alice";
let userAge = 25;
let isLoggedIn = true;

// ❌ Mauvais
let first_name = "Alice"; // snake_case (Python)
let FirstName = "Alice";  // PascalCase (réservé aux classes)

// && : retourne la première valeur fausse ou la dernière si toutes sont vraies
let result = true && "Hello"; // "Hello"
let result2 = false && "Hello"; // false

// || : retourne la première valeur vraie ou la dernière si toutes sont fausses
let name = userName || "Invité"; // Si userName est vide, utilise "Invité"

// ?? (Nullish Coalescing) : retourne la valeur de droite si la gauche est null ou undefined
let age = userAge ?? 18; // Si userAge est null/undefined, utilise 18

let age = 20;

if (age >= 18) {
  console.log("Majeur");
} else {
  console.log("Mineur");
}

// if / else if / else
if (age < 13) {
  console.log("Enfant");
} else if (age < 18) {
  console.log("Adolescent");
} else {
  console.log("Adulte");
}

let jour = "lundi";

switch (jour) {
  case "lundi":
  case "mardi":
  case "mercredi":
  case "jeudi":
  case "vendredi":
    console.log("Jour de travail");
    break;
  case "samedi":
  case "dimanche":
    console.log("Week-end");
    break;
  default:
    console.log("Jour invalide");
}

// Syntaxe : condition ? valeurSiVrai : valeurSiFaux
let age = 20;
let statut = age >= 18 ? "Majeur" : "Mineur";

// Équivalent à :
let statut;
if (age >= 18) {
  statut = "Majeur";
} else {
  statut = "Mineur";
}

// for classique
for (let i = 0; i < 5; i++) {
  console.log(i); // 0, 1, 2, 3, 4
}

// for...of (parcourir un tableau)
let fruits = ["apple", "banana", "orange"];
for (let fruit of fruits) {
  console.log(fruit); // pomme, banane, orange
}

// while
let i = 0;
while (i < 3) {
  console.log(i);
  i++;
}

// break et continue
for (let i = 0; i < 10; i++) {
  if (i === 3) continue; // Saute l'itération 3
  if (i === 7) break;    // Arrête la boucle à 7
  console.log(i);
}

// Déclaration
let fruits = ["apple", "banana", "orange"];
let nombres = [1, 2, 3, 4, 5];
let mixte = ["texte", 42, true, null]; // Peut contenir différents types

// Accès aux éléments
console.log(fruits[0]); // "pomme"
console.log(fruits[2]); // "orange"

// Longueur du tableau
console.log(fruits.length); // 3

// Modifier un élément
fruits[1] = "fraise";
console.log(fruits); // ["pomme", "fraise", "orange"]

// Tableau 2D (matrice 3x3)
let matrice = [
  [1, 2, 3],
  [4, 5, 6],
  [7, 8, 9]
];

// Accès : matrice[ligne][colonne]
console.log(matrice[1][2]); // 6

// Parcours avec boucles imbriquées
for (let i = 0; i < matrice.length; i++) {
  for (let j = 0; j < matrice[i].length; j++) {
    console.log(matrice[i][j]);
  }
}

// Fonction classique
function greet(name) {
  return `Hello ${name} !`;
}

console.log(greet("Alice")); // "Hello, Alice !"

// Fonction avec plusieurs paramètres
function add(a, b) {
  return a + b;
}

console.log(add(5, 3)); // 8

// Fonction anonyme assignée à une variable
const multiply = function(a, b) {
  return a * b;
};

console.log(multiply(4, 5)); // 20

// Syntaxe concise
const carre = (x) => x * x;
console.log(carre(5)); // 25

// Avec plusieurs paramètres
const add = (a, b) => a + b;

// Avec un corps de fonction
const greet = (name) => {
  const message = `Hello ${name}`;
  return message;
};

function greet(name = "Invité") {
  return `Hello ${name}`;
}

console.log(greet());        // "Hello, Invité"
console.log(greet("Alice")); // "Hello, Alice"

// Fonction variadique (nombre variable de paramètres)
function somme(...nombres) {
  return nombres.reduce((acc, n) => acc + n, 0);
}

console.log(somme(1, 2, 3));       // 6
console.log(somme(1, 2, 3, 4, 5)); // 15

class Personne {
  // Constructeur (appelé lors de l'instanciation)
  constructor(name, age) {
    this.name = name;   // Propriété
    this.age = age;
  }

  // Méthode
  sePresenter() {
    return `Je m'appelle ${this.name} et j'ai ${this.age} ans.`;
  }
}

// Instanciation (création d'un objet)
const alice = new Personne("Alice", 25);
console.log(alice.sePresenter()); // "Je m'appelle Alice et j'ai 25 ans."

class CompteBancaire {
  #solde; // Propriété privée (préfixe #)

  constructor(soldeInitial) {
    this.#solde = soldeInitial;
  }

  deposer(montant) {
    this.#solde += montant;
  }

  getSolde() {
    return this.#solde; // Accessible via une méthode publique
  }
}

const compte = new CompteBancaire(1000);
console.log(compte.#solde); // ❌ Erreur : propriété privée
console.log(compte.getSolde()); // ✅ 1000

class Calculatrice {
  static PI = 3.14159; // Propriété statique

  static carre(x) {    // Méthode statique
    return x * x;
  }
}

// Accès sans instanciation
console.log(Calculatrice.PI);      // 3.14159
console.log(Calculatrice.carre(5)); // 25

// ❌ Pas de 'this' dans une méthode statique

// Classe parente
class Animal {
  constructor(name) {
    this.name = name;
  }

  parler() {
    return `${this.name} fait un bruit.`;
  }
}

// Classe enfant (hérite d'Animal)
class Chien extends Animal {
  constructor(name, race) {
    super(name); // Appel du constructeur parent
    this.race = race;
  }

  // Surcharge (override) de la méthode parler
  parler() {
    return `${this.name} aboie !`;
  }

  // Nouvelle méthode spécifique
  courir() {
    return `${this.name} court vite !`;
  }
}

const rex = new Chien("Rex", "Berger Allemand");
console.log(rex.parler());  // "Rex aboie !"
console.log(rex.courir());  // "Rex court vite !"

class Compteur {
  constructor() {
    this.valeur = 0;
  }

  // Fonction classique : 'this' dépend de l'appelant
  incrementerClassique() {
    setTimeout(function() {
      this.valeur++; // ❌ 'this' ne pointe pas sur l'instance
    }, 1000);
  }

  // Arrow function : 'this' est celui du bloc englobant
  incrementerArrow() {
    setTimeout(() => {
      this.valeur++; // ✅ 'this' pointe sur l'instance
    }, 1000);
  }
}
