-- Add une column
ALTER table users ADD COLUMN phone VARCHAR(20);

-- Edit une column
ALTER table users MODIFY COLUMN name VARCHAR(150);

-- Delete une column
ALTER table users DROP COLUMN phone;

-- Add une contrainte
ALTER table users ADD CONSTRAINT fk_role
  FOREIGN KEY (role_id) REFERENCES roles(id);

-- Delete une table
DROP table users;

-- Delete if elle existe (√©vite les erreurs)
DROP table IF EXISTS users;

-- Ins√©rer un enregistrement
INSERT INTO users (name, email, age)
VALUES ('Alice', 'alice@example.com', 25);

-- Ins√©rer plusieurs enregistrements
INSERT INTO users (name, email, age) VALUES
  ('Bob', 'bob@example.com', 30),
  ('Charlie', 'charlie@example.com', 28);

-- Ins√©rer avec toutes les columns (dans l'ordre)
INSERT INTO users
VALUES (NULL, 'David', 'david@example.com', 35, NOW());

-- S√©lectionner toutes les columns
SELECT * FROM users;

-- S√©lectionner des columns sp√©cifiques
SELECT name, email FROM users;

-- Avec condition WHERE
SELECT * FROM users WHERE age >= 25;

-- Avec plusieurs conditions
SELECT * FROM users
WHERE age >= 25 AND email LIKE '%@example.com';

-- Sort les r√©sultats
SELECT * FROM users ORDER BY age DESC;

-- Limiter le number de r√©sultats
SELECT * FROM users LIMIT 10;

-- Pagination
SELECT * FROM users LIMIT 10 OFFSET 20; -- Page 3 (20 enregistrements saut√©s)

-- Delete un enregistrement
DELETE FROM users WHERE id = 1;

-- Delete plusieurs enregistrements
DELETE FROM users WHERE age < 18;

-- Vider la table (delete tous les enregistrements)
DELETE FROM users; -- ‚ö†Ô∏è Dangereux !

-- Ou mieux (plus rapide, r√©initialise les auto-incr√©ments)
TRUNCATE table users;

-- Tables d'exemple
CREATE table users (
  id INT PRIMARY KEY,
  name VARCHAR(100)
);

CREATE table orders (
  id INT PRIMARY KEY,
  user_id INT,
  amount DECIMAL(10,2),
  FOREIGN KEY (user_id) REFERENCES users(id)
);

SELECT users.name, orders.amount
FROM users
INNER JOIN orders ON users.id = orders.user_id;

-- number total d'users
SELECT COUNT(*) AS total_users FROM users;

-- √Çge moyen
SELECT AVG(age) AS age_moyen FROM users;

-- Montant total des orders
SELECT SUM(amount) AS total FROM orders;

-- D√©marrer une transaction
START TRANSACTION;

-- Op√©rations
UPDATE accounts SET balance = balance - 100 WHERE id = 1;
UPDATE accounts SET balance = balance + 100 WHERE id = 2;

-- Submit la transaction
COMMIT;

-- Ou Cancel en cas d'error
ROLLBACK;

-- Cr√©er un index
CREATE INDEX idx_email ON users(email);

-- Index sur plusieurs columns
CREATE INDEX idx_nom_prenom ON users(name, firstName);

-- Delete un index
DROP INDEX idx_email ON users;
